.nav {
  @include large {
    position: fixed;
    opacity: 0;
    left: -100%;

    transition: opacity 0.2s ease-in-out;
  }
}

.nav-btn {
  width: 25px;
  height: 25px;
  background: url(../img/menu.svg);
  background-size: contain;
  display: none;

  @include large {
    display: inline-block;
  }
}

.nav-btn__checkbox {
  @include visually-hidden;
}

.nav-btn__checkbox:checked + .nav {
  display: block;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.2);

  z-index: 9;

  opacity: 1;

  .nav__list--main {
    transform: translateX(0);
  }
}

