@mixin button {
  outline: none;
  padding: 0;
  border: none;
  background: transparent;
  cursor: pointer;
}

@mixin make-font($font-size, $line-height) {
  font-size: $font-size + px;
  line-height: $line-height / $font-size;
}

@mixin link-button {
  @include make-font(15, 20);
  color: #222;
  display: block;
  text-transform: uppercase;
  font-weight: 300;
}

